targets.expr:
sum(node_namespace_pod_container:container_cpu_usage_seconds_total:sum_irate{namespace="$namespace", pod=~"$pod"}
  * on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", workload_type="$type", pod=~"$pod"}
) by (pod)

max(sum(kube_pod_container_resource_requests{namespace="$namespace", resource="cpu", pod=~"$pod"}
  * on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", workload_type="$type", pod=~"$pod"}
) by (pod))

max(sum(kube_pod_container_resource_limits{namespace="$namespace", resource="cpu", pod=~"$pod"}
  * on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", workload_type="$type", pod=~"$pod"}
) by (pod))

sum(container_memory_working_set_bytes{namespace="$namespace", container!="", image!="", pod=~"$pod", container_name!="dynamic-config"}
  * on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", workload_type="$type", pod=~"$pod"}
) by (pod)

max(sum(kube_pod_container_resource_requests{namespace="$namespace", resource="memory", pod=~"$pod"}
  * on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", workload_type="$type", pod=~"$pod"}
) by (pod))

max(sum(kube_pod_container_resource_limits{namespace="$namespace", resource="memory", pod=~"$pod"}
  * on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", workload_type="$type", pod=~"$pod"}
) by (pod))

sum(container_memory_rss{namespace="$namespace",container!="", container_name!="dynamic-config"}
* on (namespace,pod)
group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace=~"$namespace", workload=~"$workload", workload_type="$type", pod=~"$pod"}
) by (pod)

sum by (persistentvolumeclaim) ((kube:pvc:workload:capacity{namespace="$namespace", workload=~"$workload", workload_type="$type"}
- kube:pvc:workload:available{namespace="$namespace", workload=~"$workload", workload_type="$type"})
/ kube:pvc:workload:capacity{namespace="$namespace", workload=~"$workload", workload_type="$type"} * 100)

netdata_disk_io_KiB_persec_average{cluster="$cluster", namespace="$namespace", pod=~"$pod", family=~"$ioDisk"}
* on (namespace,pod) group_left
namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", pod=~"$pod"}

netdata_disk_ops_operations_persec_average{cluster="$cluster", namespace="$namespace",pod=~"$pod", family=~"$ioDisk"}
* on (namespace,pod) group_left
namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", pod=~"$pod"}

netdata_disk_util___of_time_working_average{cluster="$cluster", namespace="$namespace",pod=~"$pod", family=~"$ioDisk"}
* on (namespace,pod) group_left
namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", pod=~"$pod"}

netdata_disk_mops_merged_operations_persec_average{cluster="$cluster", namespace="$namespace",pod=~"$pod", family=~"$ioDisk"}
* on (namespace,pod) group_left
namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", pod=~"$pod"}

count(
kube_pod_status_phase{namespace="$namespace", phase="Running"}
* on(namespace,pod)
namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", workload_type=~"$type"}
)

1 - ((
  kube_deployment_status_replicas_available{job="kube-state-metrics", namespace="$namespace", deployment="$workload"} / on (deployment, namespace) kube_deployment_spec_replicas{job="kube-state-metrics", deployment="$workload"}
) or (
  kube_statefulset_status_replicas_ready{job="kube-state-metrics", namespace="$namespace", statefulset="$workload"} / on (statefulset, namespace) kube_statefulset_status_replicas{job="kube-state-metrics", statefulset="$workload"}
))

cnch_current_metrics_manipulation{namespace="$namespace", cluster="$cluster", pod=~"$pod"}
* on (namespace,pod) group_left
namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", pod=~"$pod"}

rate(cnch_profile_events_cnch_txn_committed_total{namespace="$namespace", cluster="$cluster", pod=~"$pod"}[5m])
* on (namespace,pod) group_left
namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", pod=~"$pod"}

rate(cnch_profile_events_sd_request_upstream_total{namespace="$namespace", cluster="$cluster", pod=~"$pod"}[5m])
