namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", pod=~"$pod"}

cnch_current_metrics_tcp_connection{namespace="$namespace", cluster="$cluster", pod=~"$pod"}
* on (namespace,pod) group_left
namespace_workload_pod:kube_pod_owner:relabel{namespace="$namespace", workload=~"$workload", pod=~"$pod"}

bthread_count{namespace="$namespace", cluster="$cluster", pod=~"$pod"}
