SELECT count() FROM system.query_log WHERE (event_date = today()) AND ((event_time + toUInt64(query_duration_ms / 1000)) > (now() - 45)) AND (exception NOT LIKE '%Received from%') AND (exception LIKE '%Too many simultaneous queries%') FORMAT `Null`;
