targets.expr:
sum(
  cnch_current_metrics_query{namespace="[[namespace]]", cluster="$cluster"}
  * on (namespace,pod) group_left
  namespace_workload_pod:kube_pod_owner:relabel{namespace="[[namespace]]", workload=~".*server.*"}
) by (namespace, cluster, query_type)

sum(
  node_namespace_pod_container:container_cpu_usage_seconds_total:sum_irate{namespace="[[namespace]]"}
* on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace="[[namespace]]", workload=~"(cnch.*.worker.*default|vw-.*)"}
) by (workload, workload_type)
/sum(
  kube_pod_container_resource_limits{namespace="[[namespace]]", resource="cpu"}
* on(namespace,pod)
  group_left(workload, workload_type) namespace_workload_pod:kube_pod_owner:relabel{namespace="[[namespace]]", workload=~"(cnch.*.worker.*default|vw-.*)"}
) by (workload, workload_type)

